ror" class="alert alert-danger hidden">									<strong>Error: </strong> ya existe una noticia con ese nombre en el sistema								</div>							</div>						</div>						<div class="clearfix"></div>												<div class="col-sm-12">							<div class="form-group">								<label for="descripcion" class="control-label">Bajada de título</label>								<input type="text"" placeholder="Bajada de título" <?php echo 'value="'.$bajada.'"' ?> class="form-control" 								name="bajada" id="bajada" maxlength="100" required>							</div>						</div>						<div class="col-sm-12">							<div class="form-group">								<label for="descripcion" class="control-label">Texto</label>								<textarea id="txtDefaultHtmlArea" name="texto" cols="100" rows="15"><?php echo $texto ?></textarea>							</div>						</div>						<div class="col-sm-12">							<div class="rows">								<div class="col-sm-8 col-sm-offset-2">									<div class="form-group">										<label for="imagen1" class="control-label">Imagen</label>										<br>										<img <?php echo 'src="'.$imagen.'"'?> alt="Sin imagen" width="80%">									</div>								</div>								<div class="col-sm-4 col-sm-offset-4">									<div class="form-group">										<label for="imagen" class="control-label">Nueva Imagen (opcional, reemplazará la imagen actual)</label>										<input type="file" class="form-control" name="imagen">										</div>								</div>							</div>													</div>					</div>					<br />					<br />					<div class="modal-footer">						<a href="/admin" class="btn btn-default btn-lg"><i class="glyphicon glyphicon-remove" aria-hidden="true"></i>&nbsp;Cancelar</a>						<button class="btn btn-primary btn-lg" id="botonAgregar"><i class="glyphicon glyphicon-floppy-disk" aria-hidden="true"></i>&nbsp;Guardar</button>					</div>				</form>			</div>		</div>			</div>	</div> <!-- Bootstrap Core JavaScript --><script src="/js/bootstrap.min.js"></script><!-- Bootstrap filestyle --><script src="/js/bootstrap-filestyle.min.js"></script><script>	$(":file").filestyle({		buttonText: "Agregar imagen",		input:false		});			$("#titulo").change(function () {		nombre=$("#titulo").val();		tituloAnterior=$("#tituloAnterior").val();		console.log("nombre: "+nombre);				$.get( "verificarNombre.php", { nombre: nombre } )			.done(function( data ) {				console.log(data);				if(data=="true" && tituloAnterior!=nombre)				{					$("#divNombre").addClass(" has-error");					$("#divNombre").addClass(" has-feedback");					$("#divNombre").removeClass(" has-success");					$("#spanInput").addClass("glyphicon-remove");					$("#spanInput").removeClass("glyphicon-ok");					$("#botonAgregar").attr("disabled", "disabled");					$("#mensajeError").removeClass("hidden");				}				else 				{					$("#divNombre").removeClass(" has-error");					$("#divNombre").addClass(" has-feedback");					$("#divNombre").addClass(" has-success");					$("#spanInput").addClass("glyphicon-ok");					$("#spanInput").removeClass("glyphicon-remove");					$("#botonAgregar").removeAttr("disabled");					$("#mensajeError").addClass("hidden");				}			});			});</script></body></html>