<?php 	if($_COOKIE["agregado"]!="si"){			header("location:/login");		exit;	}include $_SERVER['DOCUMENT_ROOT']."/admin/header.php";	?><div class="container">    <div class="page-header">        <h2 class="text-center">Noticias del Sitio</h2>    </div>	<div class="table-responsive">			<table class="table table-hover table-striped">				<thead>					<tr>						<th>Tìtulo</th>						<th>Fecha agregado</th>						<th>Imagen</th>						<th>Editar</th>						<th>Eliminar</th>					</tr>				</thead>				<tbody>					<?php					//Se hace la conexion:					$con = include $_SERVER['DOCUMENT_ROOT']."/admin/crearConexion.php";					$sql="SELECT nombre, fecha, imagen FROM noticias ORDER BY fecha DESC";										$result = mysqli_query($con,$sql);					for ($i = 0; $i <$result->num_rows; $i++) {						$result->data_seek($i);						$fila = $result->fetch_assoc();						$nombreHTML = urlencode($fila["nombre"]);						echo '<tr>							<td>'.$fila["nombre"].'</td>							<td>'.$fila["fecha"].'</td>							<td class="col-sm-2"><img src="'.$fila["imagen"].'" class="img-rounded" width="60%"</td>							<td><a href="editar.php?t='.$nombreHTML.'"><span class="glyphicon glyphicon-edit text-primary"></span></a></td>							<td><a href="#" data-toggle="modal" data-target="#myModal" onclick="funcionDelete(\''.$nombreHTML.'\')">									<span class="glyphicon glyphicon-remove-circle text-danger"></span>								</a></td>						</tr>';					}					mysqli_close($con);					?>				</tbody>			</table>			<div class="modal-footer">				<a href="agregar.php" class="btn btn-lg btn-default"><span class="glyphicon glyphicon-plus text-primary"></span>&nbsp;Nueva noticia</a>			</div>		</div>	</div>	<!-- Modal -->	<div class="modal fade" id="myModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">		<div class="modal-dialog">			<div class="modal-content">				<div class="modal-header">					<button type="button" class="close" data-dismiss="modal"><span aria-hidden="true">&times;</span><span class="sr-only">Close</span></button>					<h4 class="modal-title text-center" id="myModalLabel">¿Eliminar Noticia?</h4>				</div>				<div class="modal-body">					<h5 class="text-center" id="text-modal"></h5>				</div>				<div class="modal-footer">					<button type="button" class="btn btn-default" data-dismiss="modal"><i class="glyphicon glyphicon-remove"></i>Cancelar</button>					<a href="" type="button" id="btn_delete" class="btn btn-primary"><i class="glyphicon glyphicon-ok"></i>Eliminar</a>				</div>			</div>		</div>	</div>	</div><!--body--><!-- jQuery --><script src="/js/jquery.js"></script><!-- Bootstrap Core JavaScript --><script src="/js/bootstrap.min.js"></script><!-- Para el modal --><script type="text/javascript">	function funcionDelete(name) {		$("#text-modal").html("");		var cadena = "eliminar.php?t=name";		cadena = cadena.replace("name",name);		$("#btn_delete").attr("href", cadena);		$("#text-modal").append("¿Está seguro de que desea eliminar la noticia <strong>" + name + "</strong>?");	}</script></body></html>